import React, { useState, useEffect } from 'react';
import { View, StyleSheet, Text, Image, ScrollView, TouchableOpacity } from 'react-native';
import imageToAdd from "../../../assets/paper-plane.png";
import { useNavigation } from '@react-navigation/native';

export function Ambientes() {
    const navigation = useNavigation();
    const [ambientes, setAmbientes] = useState([]);

  // Simule a criação de alguns ambientes (você pode preenchê-los com os dados reais)
  useEffect(() => {
    setAmbientes([
        { nome: 'Ambiente 1', imagem: require('caminho_para_imagem_1') },
        { nome: 'Ambiente 2', imagem: require('caminho_para_imagem_2') },
        // Adicione mais ambientes conforme necessário
    ]);
}, []);

return (
    <View style={styles.container}>
        <ScrollView contentContainerStyle={styles.contentContainer}>
            {ambientes.length === 0 ? (
                <View style={styles.centralize}>
                    <Image source={imageToAdd} style={styles.image} />
                    <Text style={styles.message}>Não há Ambientes criados</Text>
                </View>
            ) : (
                // Mapeie a lista de ambientes e exiba cada um na tela
                ambientes.map((ambiente, index) => (
                    <View key={index} style={styles.ambienteContainer}>
                        <Image source={ambiente.imagem} style={styles.ambienteImage} />
                        <Text style={styles.ambienteName}>{ambiente.nome}</Text>
                        {/* Botão "Reservar" ou outras ações, se necessário */}
                    </View>
                ))
            )}
        </ScrollView>
    </View>
);
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "white",
        justifyContent: "center",
    },
    centralize: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
    image: {
        height: 150,
        width: 150,
        marginBottom: 20,
    },
    contentContainer: {
        flexGrow: 1,
    },
    ambienteContainer: {
        flexDirection: 'row',
        alignItems: 'center',
        padding: 10,
        borderBottomWidth: 1,
        borderBottomColor: '#ccc',
    },
    ambienteImage: {
        width: 50,
        height: 50,
        borderRadius: 25, 
        marginRight: 10,
    },
    ambienteName: {
        fontSize: 18,
        flex: 1, 
    },
    reservarButton: {
        backgroundColor: '#005caa',
        borderRadius: 5,
        padding: 5,
    },
    reservarButtonText: {
        color: 'white',
    },

    addButton: {
        position: 'absolute',
        bottom: 20,
        right: 20,
        backgroundColor: '#005caa',
        width: 50,
        height: 50,
        borderRadius: 25,
        alignItems: 'center',
        justifyContent: 'center',
        elevation: 5,
    },
    addButtonText: {
        color: 'white',
        fontSize: 30,
    },
});
