import React from 'react';
import imageToAdd from "../../../assets/user.png";
import { View, StyleSheet, Button, Text, TouchableOpacity, Image } from 'react-native';

import { useNavigation } from '@react-navigation/native';

export function Perfil() {
    const navigation = useNavigation();

    return (
        <View style={styles.container}>
            <View style={styles.profileSection}>
                <View style={styles.profileBackground}>
                    <View style={styles.iconContainer}>
                        <Image source={imageToAdd} style={styles.icon} resizeMode="cover" />
                    </View>
                    <View style={styles.infoContainer}>
                        <Text style={styles.name}>Nome do Usuário</Text>
                        <Text style={styles.role}>Função do Usuário</Text>
                    </View>
                </View>
            </View>
            <View style={styles.buttonsSection}>
                <TouchableOpacity style={styles.button}>
                    <Text style={styles.buttonText}>Editar Perfil</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.button}>
                    <Text style={styles.buttonText}>Excluir Perfil</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.button}>
                    <Text style={styles.buttonText}>Deslogar</Text>
                </TouchableOpacity>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "white",
    },
    profileSection: {
        marginTop: 20,
        paddingHorizontal: 20,
    },
    profileBackground: {
        backgroundColor: '#005caa',
        flexDirection: 'row',
        alignItems: 'center',
        paddingVertical: 20,
        borderBottomWidth: 1,
        borderBottomColor: '#00357e',
    },
    iconContainer: {
        borderRadius: 75,
        padding: 10,
        marginRight: 20,
    },
    icon: {
        width: 150,
        height: 150,
        borderRadius: 75,
    },
    infoContainer: {
        flex: 1,
    },
    name: {
        fontSize: 20,
        fontWeight: 'bold',
        color: '#fff',
    },
    role: {
        fontSize: 16,
        color: '#d0e1f9',
    },
    buttonsSection: {
        paddingHorizontal: 20,
        paddingTop: 20,
    },
    button: {
        backgroundColor: '#005caa',
        borderRadius: 6,
        height: 45,
        justifyContent: 'center',
        alignItems: 'center',
        marginBottom: 12,
    },
    buttonText: {
        fontSize: 18,
        color: '#fff',
    },
});
