import React, { useState, useEffect } from 'react';
import { Alert, Button, View, StyleSheet, Text, ScrollView } from 'react-native';
import { useNavigation, useRoute } from '@react-navigation/native';

export function Reservas() {
    const route = useRoute();
    const navigation = useNavigation();
    const [reservas, setReservas] = useState([]);
    const { userEmail, userNome } = route.params;

    useEffect(() => {
        fetch(`http://localhost:8090/reserva?userEmail=${userEmail}`)
            .then(response => response.json())
            .then(data => setReservas(data))
            .catch(error => console.error(error));
    }, []);

    const handleEdit = (reserva) => {
        // Abra o modal aqui com os detalhes da reserva
    };

    const handleDelete = (id) => {
        Alert.alert(
            "Cancelar Reserva",
            "Tem certeza de que deseja cancelar esta reserva?",
            [
                {
                    text: "Não",
                    style: "cancel"
                },
                { text: "Sim", onPress: async () => {
                    try {
                        const response = await fetch(`http://localhost:8090/reserva/${id}`, {
                            method: 'DELETE',
                        });
    
                        if (response.ok) {
                            setReservas(reservas.filter(reserva => reserva.id !== id));
                        } else {
                            alert('Erro ao excluir reserva');
                        }
                    } catch (error) {
                        console.error(error);
                    }
                }}
            ]
        );
    };

