import React from 'react';
import imageToAdd from "../../../assets/user.png";
import { View, StyleSheet, Button, Text, TouchableOpacity} from 'react-native';

import { useNavigation } from '@react-navigation/native';


export function Perfil() {
    const navigation = useNavigation();
  
    return (
    <View style={styles.container}>
        <View style={styles.centralize}>
        <img src={imageToAdd} style={styles.icon} alt="Image" />
        </View>
        <View style={styles.centralize}>
        <TouchableOpacity style={styles.button}>
            <View style={styles.centralize}>
            <Text style={{fontSize: 18, color: '#fff'}}>Editar Perfil</Text>
            </View>
        </TouchableOpacity>
        
        <TouchableOpacity style={styles.button}>
            <View style={styles.centralize}>
                <Text style={{fontSize: 18, color: '#fff'}}>Excluir Perfil</Text>
            </View>
        </TouchableOpacity>

        <TouchableOpacity style={styles.button}>
            <Text style={{fontSize: 18, color: '#fff'}}>Deslogar</Text>
        </TouchableOpacity>

        </View>
    </View>
  );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        //backgroundColor: "#005caa",
        backgroundColor: "white",
        justifyContent: "center"
    },
    centralize: {
        alignItems: 'center', 
        justifyContent: 'center',
    },
    icon: {
        width: 200,
        height: 200,        
    },
    button: {
        backgroundColor: '#005caa',
        color: '#7bacd4',
        borderRadius: 6,
        width: 250,
        height: 45,
        fontSize: 20,
        margin: 12,
        padding: 10,
    },
    
})
