import React from 'react';
import { View, StyleSheet, Text, Image, ScrollView, TouchableOpacity } from 'react-native';
import imageToAdd from "../../../assets/paper-plane.png";
import { useNavigation } from '@react-navigation/native';

export function Ambientes() {
    const navigation = useNavigation();

    const handleAdicionar = () => {
        // Aqui você pode adicionar a lógica para navegar para a tela de criação de ambiente
        // navigation.navigate('TelaDeCriacaoDeAmbiente');
    };

    return (
        <View style={styles.container}>
            <ScrollView>
                <View style={styles.centralize}>
                    <Image source={imageToAdd} style={{ height: 150, width: 150 }} alt="Image" />
                    <Text style={{ fontSize: 20, marginTop: 20 }}>Não há Ambientes criados</Text>
                </View>
                {/* ... seu conteúdo da tela de ambientes ... */}
            </ScrollView>
            
            {/* Botão flutuante para adicionar */}
            <TouchableOpacity style={styles.addButton} onPress={handleAdicionar}>
                <Text style={styles.addButtonText}>+</Text>
            </TouchableOpacity>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "white",
        justifyContent: "center",
    },
    centralize: {
        alignItems: 'center', 
        justifyContent: 'center',
    },
    image: {
        width: 300,
    },
    login: {

    },
    Input: {
        backgroundColor: '#f8f4f4',
        color: '#000',
        width: 250,
        height: 45,
        fontSize: 20,
        margin: 12,
        padding: 10,
    },
    button: {
        backgroundColor: '#005caa',
        color: '#7bacd4',
        borderRadius: 6,
        width: 250,
        height: 45,
        fontSize: 20,
        margin: 12,
        padding: 10,
    }
})
