import React, { useState, useEffect } from 'react';
import imageToAdd from "../../../assets/calendar-clock.png";
import {View, Text, ScrollView, Button, Modal, TextInput} from 'react-native';

import { useNavigation, useRoute } from '@react-navigation/native';

export function Reservas() {
    const route = useRoute();
    const navigation = useNavigation();
    const [reservas, setReservas] = useState([]);
    const [modalVisible, setModalVisible] = useState(false);
    const [currentReserva, setCurrentReserva] = useState(null);
    const { userEmail, userNome } = route.params;

    useEffect(() => {
        fetch(`http://localhost:19006/reserva?userEmail=${userEmail}`)
            .then(response => response.json())
            .then(data => setReservas(data))
            .catch(error => console.error(error));
    }, []);

    const handleEdit = (reserva) => {
        setCurrentReserva(reserva);
        setModalVisible(true);
    };

    const handleCancel = (reserva) => {
        // Implemente a lógica para cancelar a reserva aqui
    };

    const handleReserveSubmit = () => {
        // Implemente a lógica para editar a reserva aqui
        setModalVisible(false);
    };

    return (
        <View style={styles.container}>
            <ScrollView contentContainerStyle={styles.contentContainer}>
                {reservas.length === 0 ? (
                    <View style={styles.centralize}>
                        <img src={imageToAdd} style={{height:150, width:150}} alt="Image" />
                        <Text style={styles.message}>Você não possui nenhuma reserva</Text>
                    </View>
                ) : (
                    reservas.map((reserva, index) => (
                        <View key={index} style={styles.reservaContainer}>
                            <Text style={styles.nomeAmbiente}>{reserva.nomeAmbiente}</Text>
                            <Text style={styles.dataHorario}>{`Data: ${reserva.data}, Horário: ${reserva.horario}`}</Text>
                            <Button title="Editar" onPress={() => handleEdit(reserva)} />
                            <Button title="Cancelar" onPress={() => handleCancel(reserva)} />
                        </View>
                    ))
                )}
            </ScrollView>

            <Modal
                animationType="slide"
                transparent={true}
                visible={modalVisible}
                onRequestClose={() => {
                    setModalVisible(!modalVisible);
                }}
            >
                <View style={styles.centeredView}>
                    <View style={styles.modalView}>
                        <Text style={styles.modalText}>Editar Reserva {currentReserva?.nomeAmbiente}</Text>
                        <TextInput
                            style={styles.input}
                            placeholder="Dia (yyyy-MM-dd)"
                            onChangeText={text => setDia(text)}
                            value={dia}
                        />
                        <TextInput
                            style={styles.input}
                            placeholder="Horário (HH:mm)"
                            onChangeText={text => setHorario(text)}
                            value={horario}
                        />
                        <TextInput
                            style={styles.input}
                            placeholder="Motivo"
                            onChangeText={text => setMotivo(text)}
                            value={motivo}
                        />
                        <Button
                            title="Salvar"
                            onPress={handleReserveSubmit}
                        />
                        <Button
                            title="Fechar"
                            onPress={() => setModalVisible(!modalVisible)}
                        />
                    </View>
                </View>
            </Modal>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "white",
    },
    centralize: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
    contentContainer: {
        flexGrow: 1,
    },
    message: {
        fontSize: 20,
    },
    reservaContainer: {
        backgroundColor: '#f8f4f4', // Cor de fundo para a reserva
        margin: 10,
        borderRadius: 10, // Borda arredondada
        padding: 10,
    },
    nomeAmbiente: {
        fontSize: 18,
        fontWeight: 'bold',
    },
    dataHorario: {
        fontSize: 16,
    },
});

// Função para adicionar uma reserva à lista (se necessário)
function adicionarReserva(novaReserva) {
