import React, { useState, useEffect } from 'react';
import { View, StyleSheet, Text, Image, ScrollView, TouchableOpacity } from 'react-native';
import imageToAdd from "../../../assets/paper-plane.png";
import { useNavigation } from '@react-navigation/native';

export function Ambientes() {
    const navigation = useNavigation();
    const [ambientes, setAmbientes] = useState([]);
    const [ambienteEditando, setAmbienteEditando] = useState(null);
    const [nome, setNome] = useState('');
    const [descricao, setDescricao] = useState('');
  
    useEffect(() => {
        fetch('http://localhost:8090/ambiente')
            .then(response => response.json())
            .then(data => setAmbientes(data));
    }, []);
  
    const handleReserve = (ambiente) => {
        // Implemente a lógica de reserva aqui
    };
  
    return (
        <View>
            {ambientes.length === 0 ? (
                <Text>Ainda não há nenhum ambiente</Text>
            ) : (
                ambientes.map((ambiente, index) => (
                    <View key={index}>
                        <Text>{ambiente.nome}</Text>
                        <Text>{ambiente.descricao}</Text>
                        <Button title="Reservar" onPress={() => handleReserve(ambiente)} />
                    </View>
                ))
            )}
            {ambienteEditando && (
                <View>
                    <TextInput value={nome} onChangeText={setNome} />
                    <TextInput value={descricao} onChangeText={setDescricao} />
                    <Button title="Salvar" onPress={handleSave} />
                </View>
            )}
        </View>
  );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "white",
    },
    centralize: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
    image: {
        height: 150,
        width: 150,
        marginBottom: 20,
    },
    contentContainer: {
        flexGrow: 1,
    },
    ambienteContainer: {
        backgroundColor: '#f8f4f4', // Cor de fundo para o quadrado
        margin: 10,
        borderRadius: 10, // Borda arredondada
        overflow: 'hidden', // Para cortar qualquer conteúdo que saia dos limites
    },
    ambienteContent: {
        alignItems: 'center',
        paddingTop: 10,
    },
    ambienteImage: {
        width: '100%',
        height: '100%',
    },
    ambienteName: {
        fontSize: 18,
        marginTop: 5,
        alignSelf: 'center'
    },
    reservarButton: {
        backgroundColor: '#005caa',
        padding: 10,
        marginTop: 2,
    },
    reservarButtonText: {
        color: 'white',
        textAlign: 'center',
    },
    message: {
        fontSize: 20,
    },
});
