import React, { useState, useEffect } from 'react';
import { View, StyleSheet, Text, Image, ScrollView, TouchableOpacity } from 'react-native';
import imageToAdd from "../../../assets/paper-plane.png";
import { useNavigation } from '@react-navigation/native';

export function Ambientes() {
    const navigation = useNavigation();
    const [ambientes, setAmbientes] = useState([]); // Estado para armazenar a lista de ambientes

    // Função para adicionar um ambiente à lista
    const adicionarAmbiente = (novoAmbiente) => {
        setAmbientes([...ambientes, novoAmbiente]);
    };

    const handleAdicionar = () => {
        // Navegue para a tela de criação de ambiente e passe a função adicionarAmbiente como parâmetro
        navigation.navigate('TelaDeCriacaoDeAmbiente', { adicionarAmbiente });
    };

    return (
        <View style={styles.container}>
            <ScrollView contentContainerStyle={styles.contentContainer}>
                {ambientes.length === 0 ? (
                    <View style={styles.centralize}>
                        <Image source={imageToAdd} style={styles.image} />
                        <Text style={styles.message}>Não há Ambientes criados</Text>
                    </View>
                ) : (
                    // Mapeie a lista de ambientes e exiba cada um na tela
                    ambientes.map((ambiente, index) => (
                        <View key={index} style={styles.ambienteContainer}>
                            <Image source={ambiente.imagem} style={styles.ambienteImage} />
                            <Text style={styles.ambienteName}>{ambiente.nome}</Text>
                            <TouchableOpacity style={styles.reservarButton}>
                                <Text style={styles.reservarButtonText}>Reservar</Text>
                            </TouchableOpacity>
                        </View>
                    ))
                )}
            </ScrollView>

            {/* Botão flutuante para adicionar */}
            <TouchableOpacity style={styles.addButton} onPress={handleAdicionar}>
                <Text style={styles.addButtonText}>+</Text>
            </TouchableOpacity>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "white",
        justifyContent: "center",
    },
    centralize: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
    image: {
        height: 150,
        width: 150,
        marginBottom: 20,
    },
    contentContainer: {
        flexGrow: 1,
    },
    ambienteContainer: {
        flexDirection: 'row',
        alignItems: 'center',
        padding: 10,
        borderBottomWidth: 1,
        borderBottomColor: '#ccc',
    },
    ambienteImage: {
        width: 50,
        height: 50,
        borderRadius: 25, 
        marginRight: 10,
    },
    ambienteName: {
        fontSize: 18,
        flex: 1, 
    },
    reservarButton: {
        backgroundColor: '#005caa',
        borderRadius: 5,
        padding: 5,
    },
    reservarButtonText: {
        color: 'white',
    },


});
