import React from 'react';
import { View, StyleSheet, Button, TextInput, TouchableOpacity, Text} from 'react-native';

import { useNavigation } from '@react-navigation/native';



export function EsqueceuSenha() {
    const navigation = useNavigation();
    
    return (
        <View style={styles.container}>
        <View style={styles.centralize}> 
            <img src={imageToAdd} style={styles.image} alt="Image" />
            <Text style={{fontSize: 25, marginBottom: 30}}>Cadastre-se</Text>
        </View>

    <View style={styles.centralize}> 
        <TextInput 
         placeholder="Email"
         style={styles.Input} />
        
        <TouchableOpacity style={styles.button}>
            <View style={styles.centralize}>
                <Text style={{fontSize: 18, color: '#fff'}}>Cadastrar</Text>
            </View> 
        </TouchableOpacity>
            <View style={styles.centralize}>    
                <TouchableOpacity onPress={() => navigation.goBack()}>
                    <Text style={{fontSize: 18, color: '#000', marginTop: 20}}>Voltar</Text>
                </TouchableOpacity>
            </View> 
    </View>
        <View style={{marginBottom: 300}}> </View>
    </View>
  );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "white",
        justifyContent: "center",
    },
    centralize: {
        alignItems: 'center', 
        justifyContent: 'center',
    },
    image: {
        width: 300,
    },
    login: {

    },
    Input: {
        backgroundColor: '#f8f4f4',
        color: '#000',
        width: 250,
        height: 45,
        fontSize: 20,
        margin: 12,
        padding: 10,
    },
    button: {
        backgroundColor: '#005caa',
        color: '#7bacd4',
        borderRadius: 6,
        width: 250,
        height: 45,
        fontSize: 20,
        margin: 12,
        padding: 10,
    }
})
