import React from 'react';
import imageToAdd from "../../../assets/teachings.png";
import { View, StyleSheet, Button, TextInput, TouchableOpacity, Text} from 'react-native';

import { useNavigation } from '@react-navigation/native';


export function Salas() {
    const navigation = useNavigation();
    const schedule = [
        { time: '7:40', room: 'Sala 1', class: 'Turma A' },
        { time: '10:30', room: 'Sala 2', class: 'Turma B' },
        // Adicione mais horários, salas e turmas conforme necessário
      ];
    return (
        <table>
          <thead>
            <tr>
              <th>Horário</th>
              <th>Sala</th>
              <th>Turma</th>
            </tr>
          </thead>
          <tbody>
            {schedule.map((item, index) => (
              <tr key={index}>
                <td>{item.time}</td>
                <td>{item.room}</td>
                <td>{item.class}</td>
              </tr>
            ))}
          </tbody>
        </table>
      );
    };

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "white",
        justifyContent: "center",
    },
    centralize: {
        alignItems: 'center', 
        justifyContent: 'center',
    },
    Input: {
        backgroundColor: '#f8f4f4',
        color: '#000',
        width: 250,
        height: 45,
        fontSize: 20,
        margin: 12,
        padding: 10,
    },
    button: {
        backgroundColor: '#005caa',
        color: '#7bacd4',
        borderRadius: 6,
        width: 250,
        height: 45,
        fontSize: 20,
        margin: 12,
        padding: 10,
    }
})
